// SELECT last("active_shards") FROM "$policy"."elasticsearch_cluster_health" WHERE ("name" =~ /^$cluster$/) AND $timeFilter GROUP BY time($__interval) fill(null)

from(bucket: "telegraf")
	|> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "elasticsearch_cluster_health")
  |> filter(fn: (r) => r._field == "active_shards")
  |> last()

// SELECT last("unassigned_shards") FROM "$policy"."elasticsearch_cluster_health" WHERE ("name" =~ /^$cluster$/) AND $timeFilter GROUP BY time($__interval) fill(null)

from(bucket: "telegraf")
	|> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "elasticsearch_cluster_health")
  |> filter(fn: (r) => r._field == "unassigned_shards")
  |> last()

// SELECT mean("active_primary_shards") FROM "$policy"."elasticsearch_cluster_health" WHERE ("name" =~ /^$cluster$/) AND $timeFilter GROUP BY time($__interval) fill(linear)

from(bucket: "telegraf")
	|> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "elasticsearch_cluster_health")
  |> filter(fn: (r) => r._field == "active_primary_shards" or  r._field == "active_shards" or r._field == "initializing_shards" or r._field == "relocating_shards" or r._field == "unassigned_shards")

// SELECT mean("open_file_descriptors") FROM "$policy"."elasticsearch_process" WHERE ("cluster_name" =~ /^$cluster$/) AND $timeFilter GROUP BY time($__interval), "node_name" fill(null)

from(bucket: "telegraf")
	|> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "elasticsearch_process")
  |> filter(fn: (r) => r._field == "open_file_descriptors")

// SELECT max("store_size_in_bytes") FROM "$policy"."elasticsearch_clusterstats_indices" WHERE ("cluster_name" =~ /^$cluster$/) AND $timeFilter GROUP BY time($__interval) fill(linear)

from(bucket: "telegraf")
	|> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "elasticsearch_clusterstats_indices")
  |> filter(fn: (r) => r._field == "store_size_in_bytes")
  |> aggregateWindow(every: v.windowPeriod, fn: max, createEmpty: false)

// SELECT mean("segments_count") FROM "$policy"."elasticsearch_indices" WHERE ("cluster_name" =~ /^$cluster$/) AND $timeFilter GROUP BY time($__interval), "node_name" fill(linear)

from(bucket: "telegraf")
	|> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "elasticsearch_indices")
  |> filter(fn: (r) => r._field == "segments_count")
  |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)

//SELECT mean("segments_doc_values_memory_in_bytes") FROM "elasticsearch_indices" WHERE $timeFilter GROUP BY time(10s) fill(null)

from(bucket: "telegraf")
	|> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "elasticsearch_indices")
  |> filter(fn: (r) => r._field =~ /^segments_/ and r._field =~ /_bytes$/)
  |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)

// SELECT max("analyze_queue") AS "Analyze", max("ccr_queue") AS "CCR", max("fetch_shard_started_queue") AS "FetchShard", max("fetch_shard_store_queue") AS "FetchShardStore", max("flush_queue") AS "Flush", max("force_merge_queue") AS "ForceMerge", max("generic_queue") AS "Generic", max("get_queue") AS "Get", max("index_queue") AS "Index", max("listener_queue") AS "Listener", max("management_queue") AS "Management", max("rollup_indexing_queue") AS "RollUPIndexing", max("search_queue") AS "Search", max("write_queue") AS "Write" FROM "$policy"."elasticsearch_thread_pool" WHERE ("cluster_name" =~ /^$cluster$/) AND $timeFilter GROUP BY time($__interval) fill(previous)

from(bucket: "telegraf")
	|> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "elasticsearch_thread_pool")
  |> filter(fn: (r) => r._field =~ /_queue/)
  |> aggregateWindow(every: v.windowPeriod, fn: max, createEmpty: false)

// SELECT mean("cpu_load_average_1m") FROM "$policy"."elasticsearch_os" WHERE ("cluster_name" =~ /^$cluster$/) AND $timeFilter GROUP BY time($__interval), "node_name" fill(null)

from(bucket: "telegraf")
	|> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "elasticsearch_os")
  |> filter(fn: (r) => r._field == "cpu_load_average_1m" or  r._field == "cpu_load_average_5m" or  r._field == "cpu_load_average_15m")
  |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)

// SELECT non_negative_derivative(sum("query_cache_total_count"), 1s) FROM "elasticsearch_clusterstats_indices" WHERE ("cluster_name" =~ /^$cluster$/) AND $timeFilter GROUP BY time($__interval) fill(null) offset 1
// SELECT non_negative_derivative(sum("query_cache_hit_count"), 1s) FROM "elasticsearch_clusterstats_indices" WHERE ("cluster_name" =~ /^$cluster$/) AND $timeFilter GROUP BY time($__interval) fill(null) offset 1
// SELECT non_negative_derivative(sum("query_cache_miss_count"), 1s) FROM "elasticsearch_clusterstats_indices" WHERE ("cluster_name" =~ /^$cluster$/) AND $timeFilter GROUP BY time($__interval) fill(null) offset 1

from(bucket: "telegraf")
	|> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "elasticsearch_clusterstats_indices")
  |> filter(fn: (r) => r._field == "query_cache_total_count" or  r._field == "query_cache_hit_count" or  r._field == "query_cache_miss_count")
  |> aggregateWindow(every: v.windowPeriod, fn: sum, createEmpty: false)
  |> derivative(nonNegative: true)

